input = ''

def magic(s):
  best = 0
  pattern = re.compile('(?:((?:.*?(a+).*?(e+).*?(i+).*?(o+).*?(u+))))')
  match = re.match(pattern, s)
  if match is None:
    return 0
  s = list(s)
  s = ''.join(s)
  while s != '':
    match = re.findall(pattern, s)
    if match:
      longestlen = 0
      for m in match:
        if len(m[0]) > longestlen:
          longestlen = len(m[0])
          longest = m
      longest = longest[1:]
      length = len(''.join(longest))
      if length > best:
        best = length
      s = s[1:]
    else:
      s = ''
  return best



